import UIKit
import SwiftUI

struct OperationConfigurator {

    static func createModule(
        output: OperationModuleOutput? = nil
    ) -> (view: some View, input: OperationModuleInput) {
        let interactor = OperationInteractor()
        let presenter = OperationPresenter(
            interactor: interactor,
            output: output
        )
        let view = OperationScreen(presenter: presenter)

        presenter.view = view
        interactor.presenter = presenter

        return (view, presenter)
    }
}
