import UIKit
import SwiftUI

struct OperationListConfigurator {

    static func createModule(
        output: OperationListModuleOutput? = nil
    ) -> (view: some View, input: OperationListModuleInput) {
        let interactor = OperationListInteractor()
        let presenter = OperationListPresenter(
            interactor: interactor,
            output: output
        )
        let view = OperationListScreen(presenter: presenter)

        presenter.view = view
        interactor.presenter = presenter

        return (view, presenter)
    }
}
